declare const categoryProducts = "\n  query categoryProducts($uid: String!,$currentPage: Int!) {\n    products(\n      search: \"\"\n      filter: { category_uid: { eq: $uid } }\n      currentPage: $currentPage\n    ) {\n      aggregations {\n        __typename\n        label\n        count\n        attribute_code\n        options {\n          __typename\n          label\n          value\n          count\n        }\n        position\n      }\n      sort_fields {\n        default\n        options {\n          label\n          value\n          __typename\n        }\n      }\n      items {\n        uid\n      }\n    }\n  }\n  \n";
export default categoryProducts;
